<?php//создадим класс юзер и в классе укажемclass User{    //задаем свойства    public $name;    protected $login;    public $surname;    private $mail;    //определяем методы    public function getName(){        //тело функции        return $this -> name;    }    public function getSurname(){        return $this -> surname;    }    public function sendMail(){        //тело функции    }}//создаем объект юзер$User = new User;//Задаем значение свойства name равное Админ$User->name = 'Элина';//обращаемся в функции getNameecho $User->getName();$User->surname = 'Разина';echo $User->getSurname();echo '<br />';//Статические методы - без создания экземпляра классаclass Human {    static protected $hand = 2;    static protected $leg     = 2;    static protected $head = 1;    public static function getInfo() {        echo "У человека " . self::$hand . " руки, " . 	self::$leg . " ноги и " . self::$head . " голова";    }    public function sayHello() {        echo "Здравствуй, человек!";    }}Human::getInfo();Human::sayHello();class Guest{    public static $id = 0;    public static function sayHello(){        echo "Здравствуй гость! Зарегистрируйся!";    }    public static function getId(){        echo self::$id;    }}Guest::sayHello();Guest::getId();$guest = new Guest;$guest::sayHello();?>