<?phpinterface Show{    public function show();}abstract class Publications Implements Show{    public $name;    abstract function show();    public function doPrint(){}    public function save(){}}//реализация класса News, наследуемого от класса Publicationsclass News extends Publications{    public $name = "Новость";    public function show(){        echo "<h4>{$this->name}</h4>h4>";    }}//реализация класса Article, наследуемого от Publicationsclass Article extends Publications{    public $name = "Статья";    public function show(){        echo"<h4>{$this->name}</h4>";    }}//реализация класса Annpuncementclass Announcement{    public $name = "Анонс";}//Наполняем массив публикаций объектами, производными от Publications$publications[] = new News();$publications[] = new Article();$publications[] = new Announcement();foreach($publications as Publication){    $publication (interface of Publication){        $publication->show();    }    else{        echo "Неизвестный тип статьи";    }}?>